<?phpnamespace Kizilare\ServicesDebug\Command;use Kizilare\ServicesDebug\Helper\ConfigurationHelper;use Kizilare\ServicesDebug\Helper\DependenciesHolderHelper;use Symfony\Bundle\FrameworkBundle\Command\ContainerAwareCommand;use Symfony\Component\Console\Input\InputArgument;use Symfony\Component\Console\Input\InputInterface;use Symfony\Component\Console\Output\OutputInterface;class MethodsUsesCommand extends ContainerAwareCommand{    /**     * {@inheritdoc}     */    protected function configure()    {        $this            ->setName('kizilare:methods')            ->addArgument('config', InputArgument::REQUIRED, 'Configuration file to use')            ->setDescription('Services and classes dependencies path');    }    /**     * {@inheritdoc}     */    protected function execute(InputInterface $input, OutputInterface $output)    {        $configurationFile = $input->getArgument('config');        $configuration = new ConfigurationHelper($configurationFile);        $this->getContainer()->get('kizilare.methods_processor')->processSource($configuration);    }}