<?phpnamespace Kizilare\ServicesDebug\Command;use Kizilare\ServicesDebug\Helper\ConfigurationHelper;use Kizilare\ServicesDebug\Helper\DependenciesHolderHelper;use Kizilare\ServicesDebug\Processor\MethodsProcessor;use Symfony\Bundle\FrameworkBundle\Command\ContainerAwareCommand;use Symfony\Component\Console\Command\Command;use Symfony\Component\Console\Input\InputArgument;use Symfony\Component\Console\Input\InputInterface;use Symfony\Component\Console\Output\OutputInterface;class MethodsUsesCommand extends Command{    /**     * {@inheritdoc}     */    protected function configure()    {        $this            ->setName('kizilare:methods')            ->addArgument('config', InputArgument::REQUIRED, 'Configuration file to use')            ->setDescription('Services and classes dependencies path');    }    /**     * {@inheritdoc}     */    protected function execute(InputInterface $input, OutputInterface $output)    {        $configurationFile = $input->getArgument('config');        $configuration = new ConfigurationHelper($configurationFile);        $methodsProcessor = new MethodsProcessor($configuration);        $methodsProcessor->processSource();    }}